from aip.query_code import delete_codefrom data_assembly.data_request import get_requestfrom data_assembly.data_url import get_urlfrom utils.Request_method import get_tmethodfrom utils.log_utils import loggerdef test_send_code():    '''    ������֤�����    :return:    '''    # 1. ��ȡ������֤��� URL    url = get_url.get_pytest_login_url()    # 3. ��ȡ������    json_data=get_request.get_login_request()    logger.info(f"发送短信验证码的参数：{json_data}")    # 4. ��������    try:        # 删除数据库手机号信息        delete_code(json_data['mobile'])        result = get_tmethod.Requestmethod_post(url, json=json_data)        logger.info(f": 发送的验证码的手机号：{result['mobile']}")        return get_request.get_login_request()['mobile']    except Exception as e:        logger.error(f"��������쳣: {e}")    if __name__ == '__main__':        test_send_code(18000000099)